const text = $json.message.text.toLowerCase();
const filters = {};

if (text.includes("cb")) filters.position = "CB";
if (text.includes("st")) filters.position = "ST";
if (text.includes("pace")) filters.pace = text.match(/pace\s*(>=|>|=)?\s*(\d+)/)?.[0];
if (text.includes("finishing")) filters.finishing = text.match(/finishing\s*(>=|>|=)?\s*(\d+)/)?.[0];

return [{ json: filters }];

const filters = $input.item.json;

function parseCondition(condition) {
  if (typeof condition !== 'string') return null;
  const match = condition.match(/(>=|<=|>|<|=)?\s*(\d+)/);
  if (!match) return null;
  return { operator: match[1] || '=', value: parseFloat(match[2]) };
}

function evaluate(operator, playerValue, conditionValue) {
  playerValue = parseFloat(playerValue);
  switch (operator) {
    case '>': return playerValue > conditionValue;
    case '>=': return playerValue >= conditionValue;
    case '<': return playerValue < conditionValue;
    case '<=': return playerValue <= conditionValue;
    case '=': return playerValue === conditionValue;
    default: return false;
  }
}

return items.filter(item => {
  const player = item.json;
  const pos = (player["Position Asli"] || "").toLowerCase();
  if (filters.position && !pos.includes(filters.position.toLowerCase())) return false;

  for (const key of Object.keys(filters)) {
    if (key === 'position') continue;
    const playerValue = player[key.charAt(0).toUpperCase() + key.slice(1)];
    const parsed = parseCondition(filters[key]);
    if (!parsed || !evaluate(parsed.operator, playerValue, parsed.value)) return false;
  }

  return true;
}).slice(0, 1);

